package first_unique_character

import (
	"fmt"
	"reflect"
	"testing"
)

var testCases = []struct {
	input string
	want  int
}{
	{input: "leetcode", want: 0},
	{input: "loveleetcode", want: 2},
	{input: "aabb", want: -1},
	{input: "acabbc", want: -1},
	{input: "a", want: 0},
	{
		input: "",
		want:  1615,
	},
}

func Test_countGoodSubstrings(t *testing.T) {
	for i, tc := range testCases {
		got := firstUniqChar(tc.input)
		if !reflect.DeepEqual(tc.want, got) {
			t.Fatalf("Case #%d: expected: %v, got: %v", i+1, tc.want, got)
		}
	}
}

func Test_countGoodSubstringsMaps(t *testing.T) {
	for i, tc := range testCases {
		got := firstUniqCharMaps(tc.input)
		if !reflect.DeepEqual(tc.want, got) {
			t.Fatalf("Case #%d: expected: %v, got: %v", i+1, tc.want, got)
		}
	}
}

func Benchmark_countGoodSubstrings(b *testing.B) {
	for i, bc := range testCases {
		b.Run(fmt.Sprintf("Case_%d", i), func(b *testing.B) {
			for i := 0; i < b.N; i++ {
				firstUniqChar(bc.input)
			}
		})
	}
}

func Benchmark_countGoodSubstringsMaps(b *testing.B) {
	for i, bc := range testCases {
		b.Run(fmt.Sprintf("Case_%d", i), func(b *testing.B) {
			for i := 0; i < b.N; i++ {
				firstUniqCharMaps(bc.input)
			}
		})
	}
}
